import { createContext } from "react";

export const initialUserData = {
  id: "",
  token: "",
  logged_in: false,
  first_name: "",
  last_name: "",
  email: "",
  project_pref: [],
  skills: [],
  team_id: "",
};

export function reducer(state, userData) {
  return {
    ...state,
    id: userData.id,
    first_name: userData.first_name,
    logged_in: userData.logged_in,
  };
}

export const UserContext = createContext();
